# installation path setting
bin_path="/ihp/ihpusr/cadence/xcelium/22.03/tools.lnx86/bin"

# set xmelab command line args
xmelab_opts="-64bit -relax -access +rwc -namemap_mixgen -messages -logfile elaborate.log"

# set design libraries
design_libs_elab="-libname xil_defaultlib -libname secureip"

# set fault injection parameters
fi_opts="-fault_dbg -fault_top Scrambler_32bits_TB -fault_overwrite -fault_file ./fault_list " 

# run elaboration
$bin_path/xmelab $xmelab_opts $fi_opts $design_libs_elab xil_defaultlib.Scrambler_32bits_TB
